import React from "react";
import {Grid, Image, Text} from "../elements";

const Post = (props) =>{
    return(


        <React.Fragment>
            <Grid is_flex padding ="16px">
                <Grid is_flex>
                    <Image shape="circle" src={props.src}/>
                    <Text bold>{props.user_info.user_name}</Text>
                    <Text>{props.insert_dt}</Text>

                </Grid>
                <Grid padding = "16px">
                    <Text>{props.contents}</Text>
                </Grid>
                <Grid>
                    <Image shape ="rectangle" src={props.src}/>
                </Grid>
                <Grid padding="16px">
                    <Text margin ="0px" bold>댓글{props.comment_cnt}개</Text>
                </Grid>

            </Grid>
        </React.Fragment>
    )

}

Post.defaultProps ={
    user_info:{
        user_name: "joy",
        user_profile: "ttps://www.vmcdn.ca/f/files/piquenewsmagazine/import/2015-33_news_mtnnews2-1-cd1c0a5e13e77f75.jpg;w=640",

    },
    image_url:"ttps://www.vmcdn.ca/f/files/piquenewsmagazine/import/2015-33_news_mtnnews2-1-cd1c0a5e13e77f75.jpg;w=640",
    contents:"Love Lake Luise",
    comment_cnt: 22,
    insert_dt: "2021-03-28 10:00:00",
};

export default Post;
