import styled from 'styled-components';
import React from "react";


const Image = (props) => {
    const {shape, src, size} = props;

    const styles = {
        src: src,
        size: size,
    }

    if(shape === "circle"){
        return(
            <ImageCircle {...styles}>

            </ImageCircle>
        )
    }


    if(shape === "rectangle"){
        return(
           <AspectOutter>
               <AspectInner {...styles}>
               </AspectInner>
           </AspectOutter>
        )
    }



    return (
        <React.Fragment>

        </React.Fragment>
    )
}

Image.defaultProps = {
    shape: "circle",
    src: "https://www.vmcdn.ca/f/files/piquenewsmagazine/import/2015-33_news_mtnnews2-1-cd1c0a5e13e77f75.jpg;w=640",
    size: 36,



}


const AspectOutter = styled.div`
    width: 100%;
    min-width: 250px;
`;


const AspectInner = styled.div`
    position: relative;
    padding-top: 75%;
    overflow: hidden;
    background-image: url("https://www.vmcdn.ca/f/files/piquenewsmagazine/import/2015-33_news_mtnnews2-1-cd1c0a5e13e77f75.jpg;w=640");
    background-size: cover;

`;




const ImageCircle = styled.div`

--size: ${(props) => props.size}px;
    width: var(--size);
    height: var(--size);
    border-radius: var(--size);

    background-image: url("${(props) => props.src}");
    background-size: cover;
    margin: 4px;

`;



export default Image;
