import React from "react";
import Grid from "../elements/Grid";
import Image from "../elements/Image";
import Text from "../elements/Text";


const Post = (props) =>{
    return(


        <React.Fragment>
            <Grid padding ="16px">
                <Grid is_flex>
                    <Image shape="circle" src={props.src}/>
                    <Text bold>{props.user_info.user_name}</Text>
                    <Text>{props.insert_dt}</Text>

                </Grid>
                <Grid padding = "16px">
                    <Text>{props.contents}</Text>
                </Grid>
                <Grid>
                    <Image shape ="rectangle" src={props.src}/>
                </Grid>
                <Grid padding="16px">
                    <Text bold> {props.comment_cnt}</Text>
                </Grid>

            <div>user profile / user name / insert_dt </div>
            <div>contents</div>
            <div>image</div>
            <div>comment cnt</div>

            </Grid>
        </React.Fragment>
    )

}

Post.defaultProps ={
    user_info:{
        user_name: "joy",
        user_profile: "https://cdn.vox-cdn.com/thumbor/_niIASwZl7STWph6JfE2_zj3WEM=/0x0:1800x1350/1200x800/filters:focal(756x531:1044x819)/cdn.vox-cdn.com/uploads/chorus_image/image/66939756/71360_alt2.0.jpeg",

    },
    image_url:"https://cdn.vox-cdn.com/thumbor/_niIASwZl7STWph6JfE2_zj3WEM=/0x0:1800x1350/1200x800/filters:focal(756x531:1044x819)/cdn.vox-cdn.com/uploads/chorus_image/image/66939756/71360_alt2.0.jpeg",
    contents:"LEGO lover",
    comment_cnt: 22,
    insert_dt: "2021-03-28 10:00:00",
};

export default Post;
